data ArrayKW = KeyWordTypeArray
  deriving (Eq, Ord, Show, Read)

data Program = ProgramStart ProgramKW Ident InnerBlockWithDecl
  deriving (Eq, Ord, Show, Read)

data InnerBlockWithDecl
