  deriving (Eq, Ord, Show, Read)

data InnerBlockExec
    = InnerBlockOnlyExecution BeginKW NonMandatoryTerminator [Statement] EndKW
  deriving (Eq, Ord, Show, Read)

data NonMandatoryTerminator
