  deriving (Eq, Ord, Show, Read)

data ProcedureDecl
    = ProcedureDeclaration ProcedureKW Ident DeclarationFunc InnerBlockWithDecl
  deriving (Eq, Ord, Show, Read)

data FunctionCall = FunctionCall Ident [RightExp]
  deriving (Eq, Ord, Show, Read)

data ProcedureCall = ProcedureCall Ident [RightExp]
